
#pragma once
#include <Arduino.h>

const String TIMESET = "";
const String INDEX = "<!DOCTYPE html><html><script>    function sync(change)    {        var date = Date.now() % 86400000        var xhr = new XMLHttpRequest();        xhr.open('POST', 'http://192.168.4.1/sync', true);        xhr.setRequestHeader('Content-Type', 'application/x-www-form-urlencoded');        xhr.send('sync=' + date.toString);                if (!change && xhr.status == 200) {                        window.alert('Time synchronized successfully');        } else if (!change) {            window.alert('Error synchronizing time');        }    }    function timechange()    {        sync(true);        window.location.replace('192.168.4.1/timeedit');    }    function refresh(refreshPeriod)     {        setTimeout('location.reload(true)', refreshPeriod);    }    window.onload = refresh(5000);    </script>   <head>    <meta charset='utf-8'>    <meta http-equiv='X-UA-Compatible' content='IE=edge'>    <meta http-equiv=\'refresh\' content=\'5\'>    <title>SmartTerrarium Dashboard</title>    <meta name='viewport' content='width=device-width, initial-scale=1'>    <link rel='stylesheet' type='text/css' media='screen' href='main.css'></head><body><h1>SmartTerrarium Dashboard</h1><h2>Terrarium</h2><b>Current Temperature:</b> %%temp%% <br><b>Current Humidity:</b> %%humid%%<h2>System</h2><b>Light:</b> %%light%% <br><b>Terrarium Time:</b> %%terrariumtime%% <br><button onclick='timechange()'>Edit Time</button><button onclick='sync(false)'>Sync Time</button></body></html>";